        adj_list[a - 1].append(b - 1)
        adj_list[b - 1].append(a - 1)

    def dfs(v, visited, low, parent, bridges):
        visited[v] = True
        low[v] = parent[v] = v

        for u in adj_list[v]:
            if not visited[u]:
                parent[u] = v
                low[u] = v
                dfs(u, visited, low, parent, bridges)

                if low[u] == v:
                    bridges[v] = True

                low[v] = min(low[v], low[u])
            elif u != parent[v]:
                low[v] = min(low[v], parent[u])

    visited = [False] * N
    low = [0] * N
    parent = [-1] * N
    bridges = [False] * N

    for v in range(N):
        if not visited[v]:
            dfs(v, visited, low, parent, bridges)

    return sum(1 for b in bridges if b)

