def solution(stdin: str) -> str:
    def cross_product(a: list, b: list) -> list:
        return [a[1] * b[2] - a[2] * b[1], a[2] * b[0] - a[0] * b[2], a[0] * b[1] - a[1] * b[0]]

    def dot_product(a: list, b: list) -> float:
        return sum(i * j for i, j in zip(a, b))

    def magnitude(a: list) -> float:
        return (dot_product(a, a) ** 0.5)

    def angle(a: list, b: list) -> float:
        return (dot_product(a, b) / (magnitude(a) * magnitude(b)))

    def radians_to_degrees(radians: float) -> float:
        return (radians * 180) / math.pi

    a, b, c, d = [float(x) for x in stdin.split()]
    A, B, C, D = [[a, b, c], [a, b, d], [a, c, b], [b, c, d]]

    AB = [B[i] - A[i] for i in range(3)]
    BC = [C[i] - B[i] for i in range(3)]
    CD = [D[i] - C[i] for i in range(3)]

    X = cross_product(AB, BC)
    Y = cross_product(BC, CD)

    cos_phi = dot_product(X, Y) / (magnitude(X) * magnitude(Y))
    phi = math.acos(cos_phi)

    return f"{radians_to_degrees(phi):.2f}"

