def solution(stdin: str) -> str:
    N, M = map(int, stdin.split()[:2])
    H = list(map(int, stdin.split()[2:N+2]))
    A, B = zip(*[map(int, stdin.split()[N+2+2*i:N+4+2*i]) for i in range(M)])
    adjacency_list = [[] for _ in range(N)]

    for i in range(M):
        adjacency_list[A[i] - 1].append(B[i] - 1)
        adjacency_list[B[i] - 1].append(A[i] - 1)

    def dfs(node: int, visited: list[bool], elevation: int) -> int:
        visited[node] = True
        max_elevation = elevation
        for neighbor in adjacency_list[node]:
            if not visited[neighbor]:
                max_elevation = max(max_elevation, dfs(neighbor, visited, elevation))
        return max_elevation

    good_observatories = 0
    visited = [False] * N

    for i in range(N):
        if not visited[i]:
            max_elevation = dfs(i, visited, H[i])
            good = True
            for neighbor in adjacency_list[i]:
                if max_elevation < H[neighbor]:
                    good = False
                    break
            if good:
                good_observatories += 1

    return str(good_observatories)

