    # Replace first letter with nearest hard consonant (choose nearest to 'A' if there is a tie)
    first_letter_map = {'b': 'd', 'c': 'd', 'd': 'd', 'g': 'j', 'k': 't', 'n': 't', 'p': 't', 't': 't'}

    # Map of remaining hard consonants to their nearest hard consonants
    hard_consonant_map = {'f': 'd', 'h': 'h', 'j': 'j', 'l': 't', 'm': 'n', 's': 's', 'v': 'f', 'x': 's', 'z': 's'}

    # Map of remaining vowels to their nearest vowels
    vowel_map = {'a': 'o', 'e': 'o', 'i': 'uh', 'o': 'o', 'u': 'ah'}

    # Split sentence into words
    words = stdin.split()

    # Iterate over words and translate each word to nimionese
    for i, word in enumerate(words):
        # Remove dashes from word
        word = word.replace('-', '')

        # Replace 'each' with 'Dach'
        if word == 'each':
            word = 'Dach'

        # Initialize nimion word as empty string
        nimion_word = ''

        # Iterate over syllables in word
        for j, syllable in enumerate(word.split()):
            # Get first letter of syllable
            first_letter = syllable[0]

            # If first letter is hard consonant, replace with nearest hard consonant
            if first_letter.lower() in first_letter_map:
                first_letter = first_letter_map[first_letter.lower()]

            # If first letter is a hard consonant, add it to nimion word
            if first_letter.lower() in hard_consonant_map:
                nimion_word += first_letter.lower()

            # If first letter is a vowel, add nearest vowel to nimion word
            elif first_letter.lower() in vowel_map:
                nimion_word += vowel_map[first_letter.lower()]

            # If first letter is a consonant, add nearest hard consonant to nimion word
            else:
                nimion_word += first_letter.lower()

            # If there are more than one syllable in the word, add same consonant as first syllable
            if j > 0:
                first_letter = word.split()[j-1][0]
                if first_letter.lower() in hard_consonant_map:
                    nimion_word += first_letter.lower()

        # Add final 'ah', 'oh', or 'uh' to word
        if nimion_word[-1].lower() in {'a', 'e', 'i', 'o', 'u'}:
            if nimion_word[-1].lower() in {'a', 'e', 'i', 'o'}:
                nimion_word += 'o'
            else:
                nimion_word += 'a'
        else:
            nimion_word += 'h'

        # Add nimion word to list of words
        words[i] = nimion_word

    # Join words back into sentence and return
    return ' '.join(words)

