    if n == 1:
        return str(1)
    if k == 1:
        return str(n)
    if k == 2:
        return str(n - 1 + 1)

    # We need to find the expected score for each number of rolls
    # We can use dynamic programming to solve this
    def expected_score(d, n, k):
        if d == 0:
            return 0
        if d == 1:
            return n

        memo = [[0] * (k + 1) for _ in range(d + 1)]
        for i in range(1, k + 1):
            memo[1][i] = n

        for d in range(2, d + 1):
            for i in range(1, k + 1):
                for j in range(1, i + 1):
                    memo[d][i] = max(memo[d][i], j * n + (i - j) * memo[d - 1][i - j])

        return memo[d][k]

    return str(expected_score(k, n, k))

