def solution(stdin: str) -> str:
    input_str = stdin.strip()
    S = int(input_str)

    # Base cases
    if S == 1:
        return f"{S}:"

    def is_valid(num_rows, stars_per_row):
        if num_rows == 1:
            return stars_per_row > 0
        else:
            return stars_per_row > 0 and stars_per_row <= num_rows

    def generate_patterns(num_rows, stars_per_row):
        if num_rows == 1:
            yield stars_per_row
        else:
            for i in range(stars_per_row, num_rows + 1):
                yield i

    # Find all valid patterns
    patterns = []
    for num_rows in range(2, S + 1):
        for stars_per_row in range(1, num_rows + 1):
            if is_valid(num_rows, stars_per_row):
                patterns.extend(generate_patterns(num_rows, stars_per_row))

    # Sort patterns
    patterns.sort()

    # Generate compact representations
    result = f"{S}:"
    for pattern in patterns:
        if pattern == 1:
            continue
        num_rows = len(str(pattern))
        max_stars_per_row = int(pattern / num_rows)
        min_stars_per_row = max_stars_per_row - 1
        while min_stars_per_row >= 1:
            if is_valid(num_rows, min_stars_per_row):
                break
            min_stars_per_row -= 1

        if min_stars_per_row < 1:
            continue

        compact_representation = f"{min_stars_per_row},{min_stars_per_row + max_stars_per_row}"
        result += f"\n{compact_representation}"

    return result

