                            if is_inside((x, y), window) and window[x][y] == '*')
                        if len(racket) > len(best_racket):
                            best_racket = racket
        return best_racket

    racket = find_racket(flies)
    max_hits = sum(hit(fly, racket) for fly in flies)
    return str(max_hits) + '\n' + '\n'.join(
        row if row.find('+') == -1 else row[:row.index('+')] + '|' + row[row.index('+') + 1:]
