    frame2 = stdin.split('\n')[1 + M + 8:1 + 2 * M + 8]

    # Find the position of the falcon in the first frame
    for row in range(M):
        for col in range(N):
            if frame1[row][col] == C:
                row1, col1 = get_position(row1, col1, 'up', row - row1)
