                row2, col2 = get_position(row2, col2, 'up', row - row2)
                break

    # Calculate the distance between the two frames
    distance = sqrt((row1 - row2) ** 2 + (col1 - col2) ** 2)

    # Calculate the speed of the camera
    speed = distance / (frame2[row2][col2 + 1] - frame1[row1][col1 + 1])

    # Calculate the position of the falcon in the result frame
    row, col = get_position(row1, col1, 'up', row2 - row1)

