    for i in range(1, n+1):
        row = list(map(int, input_lines[i].split()))
        art_matrix.append(row)

    # Initialize a table to store the shortest distances from each tile to the target tile (k).
    table = [[float('inf')]*n for _ in range(n)]
    table[0][art_matrix[0].index(1)] = 0

    # Fill the table using dynamic programming.
    for i in range(1, n):
        for j in range(n):
            if art_matrix[i][j] != 0:
                for x, y in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                    x_new, y_new = j+x, i+y
                    if 0 <= x_new < n and 0 <= y_new < n and art_matrix[y_new][x_new] != 0:
                        table[i][j] = min(table[i][j], table[y_new-1][x_new]+abs(y-y_new)+abs(x-x_new))

    # Check if the target tile (k) is reachable.
