    def area(polygon: List[Tuple[float, float]]) -> float:
        total = 0
        for i in range(len(polygon)):
            j = (i + 1) % len(polygon)
            xi, yi = polygon[i]
            xj, yj = polygon[j]
            total += xi * yj - xj * yi
        return abs(total) / 2

    def resize_polygon(polygon: List[Tuple[float, float]], target_area: float) -> List[Tuple[float, float]]:
        current_area = area(polygon)
        if current_area == target_area:
            return polygon

        side_length = (current_area / target_area) ** 0.5
        new_polygon = [(x + y, y - x) for x, y in polygon]
        for i in range(len(new_polygon)):
            j = (i + 1) % len(new_polygon)
            new_polygon[i] = (side_length * (new_polygon[i][0] + new_polygon[j][0]) / 2, side_length * (new_polygon[i][1] + new_polygon[j][1]) / 2)

        return new_polygon

    def move_to_origin(polygon: List[Tuple[float, float]]) -> List[Tuple[float, float]]:
        min_x = min(x for x, y in polygon)
        min_y = min(y for x, y in polygon)
        return [(x - min_x, y - min_y) for x, y in polygon]

    def to_string(polygon: List[Tuple[float, float]]) -> str:
        return "\n".join(f"{x} {y}" for x, y in polygon)

