        return a*x + b*y

    def is_valid(x, y):
        return x >= 1 and y >= 1 and x + y <= m and 2*x + y >= sigma

    def max_rent():
        max_x = (m - 2) // 2
        max_y = (m - sigma) // 2
        return f(max_x, max_y)

    x, y = 0, 0
    while not is_valid(x, y):
        x += 1
        y = (m - x) // 2

    return max_rent()


