        if col < m - 1:
            adjacent_cells.append((row, col + 1))
        return adjacent_cells

    def is_valid_placement(coffee_shops: list, row: int, col: int) -> bool:
        for shop in coffee_shops:
            if abs(row - shop[0]) <= 1 and abs(col - shop[1]) <= 1:
                return False
        return True

    def backtrack(coffee_shops: list, row: int, col: int, n: int, m: int, result: str) -> None:
        if row == n - 1 and col == m - 1:
            result = ''.join(result)
            print(result)
            return
        if col == m - 1:
            row += 1
            col = 0
        if row > n - 1 or col > m - 1:
            return

        if map[row][col] == '.' and is_valid_placement(coffee_shops, row, col):
            result[row][col] = 'E'
            coffee_shops.append((row, col))
            backtrack(coffee_shops, row, col + 1, n, m, result)
            result[row][col] = '.'
            coffee_shops.remove((row, col))

        backtrack(coffee_shops, row, col + 1, n, m, result)

    n, m = map(int, stdin.split()[0].split(','))
