    for a, b in similarities:
        movie_similarities[a].add(b)
        movie_similarities[b].add(a)

    def get_horror_index(movie_id):
        if movie_id in horror_list:
            return 0
        worst_similar_index = float('inf')
        for similar_movie_id in movie_similarities[movie_id]:
            if similar_movie_id in horror_list:
                current_similar_index = 1
            else:
                current_similar_index = get_horror_index(similar_movie_id)
                if current_similar_index < worst_similar_index:
                    worst_similar_index = current_similar_index + 1
        return worst_similar_index

    highest_horror_index = -float('inf')
    highest_horror_movie_id = -1
    for movie_id in range(N):
        current_horror_index = get_horror_index(movie_id)
        if current_horror_index > highest_horror_index:
            highest_horror_index = current_horror_index
            highest_horror_movie_id = movie_id
        elif current_horror_index == highest_horror_index:
            highest_horror_movie_id = min(highest_horror_movie_id, movie_id)

    return str(highest_horror_movie_id)


