    keyphrase = keyphrase.replace(" ", "").replace("q", "").upper()
    plaintext = plaintext.replace(" ", "").upper()

    # Generate encryption key
    key = "".join(keyphrase)
    key += "".join(sorted(set("abcdefghijklmnopqrstuvwxyz") - set(key)))
    encryption_table = [key[i:i+5] for i in range(0, 25, 5)]

    # Encrypt plaintext
    encrypted_text = ""
    for i in range(0, len(plaintext), 2):
        digraph = plaintext[i:i+2]
        if digraph[0] == digraph[1]:
            encrypted_text += digraph[0] + "X"
        else:
            row1, col1, row2, col2 = 0, 0, 0, 0
            for row in range(5):
                if digraph[0] in encryption_table[row]:
                    row1, col1 = row, encryption_table[row].index(digraph[0])
            for row in range(5):
                if digraph[1] in encryption_table[row]:
                    row2, col2 = row, encryption_table[row].index(digraph[1])
            if row1 == row2:
                encrypted_text += encryption_table[row1][(col1 + 1) % 5] + encryption_table[row1][(col2 + 1) % 5]
            elif col1 == col2:
                encrypted_text += encryption_table[(row1 + 1) % 5][col1] + encryption_table[(row2 + 1) % 5][col2]
            else:
                encrypted_text += encryption_table[row1][col2] + encryption_table[row2][col1]

    return encrypted_text

