def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    student_counts = list(map(int, stdin.splitlines()[1].split()))

    for i in range(1, n):
        if student_counts[i-1] >= student_counts[i]:
            return "impossible"

    order = []
    pile = sum(student_counts)
    for i in range(n):
        if pile == 0:
            return "impossible"
        order.append(i + 1)
        pile -= student_counts[i]

        for j in range(i+1, n):
            if pile >= student_counts[j]:
                order.append(j + 1)
                pile -= student_counts[j]
            else:
                break

    if pile > 0:
        return "impossible"

    return " ".join(map(str, order))

