def solution(stdin: str) -> str:
    from collections import defaultdict
    from itertools import combinations

    n = int(stdin.splitlines()[0])
    graph = defaultdict(set)
    for i in range(1, n):
        a, b = map(int, stdin.splitlines()[i+1].split())
        graph[a].add(b)
        graph[b].add(a)

    def dfs(node, visited, path, result):
        visited[node] = True
        path.append(node)

        for neighbor in graph[node]:
            if not visited[neighbor]:
                dfs(neighbor, visited, path, result)

        result.append(path.copy())
        path.pop()
        visited[node] = False

    def find_max_edges(paths):
        edges = set()
        for path in paths:
            edges |= set(zip(path, path[1:]))
        return len(edges)

    visited = [False] * (n + 1)
    paths = []
    for i in range(1, n + 1):
        if not visited[i]:
            path = []
            dfs(i, visited, path, paths)

    max_edges = find_max_edges(paths)

    for a, b, c in combinations(range(1, n + 1), 3):
        if a != b != c:
            edges = set()
            for path in paths:
                edges |= set(zip(path, path[1:]))

            count = sum(1 for edge in edges if edge in ((a, b), (b, c), (a, c)))

            if count > max_edges:
                max_edges = count
                result = [a, b, c]

    return max_edges, result

