        for j in range(n):
            if available_friends[j] and (friends[i-1] == 0 or friends[i-1] == j+1):
                return j+1
        return None

    def find_friend_to_receive(i):
        for j in range(n):
            if available_friends[j] and (friends[j] == 0 or friends[j] == i):
                return j+1
        return None

    for i in range(1, n+1):
        if friends[i-1] > 0:
            friend_to_give = find_friend_to_give(i)
