def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    exams = [list(map(int, stdin.split()[i:i+3])) for i in range(2, 2*(m+1), 3)]
    days = list(range(1, n+1))

    def prepare_and_pass(day, exam_idx):
        days[exams[exam_idx][1]-1] = 0
        days[exams[exam_idx][2]-1] = 0
        days[day-1] = exam_idx + 1

    preparing_days = set()
    for exam_idx in range(m):
        s, d, c = exams[exam_idx]
        if s > d or c > n:
            return -1
        if c == 1:
            prepare_and_pass(days.index(d-1), exam_idx)
        elif c > 1:
            for day in range(s-1, d-c):
                if day not in preparing_days:
                    prepare_and_pass(days.index(day), exam_idx)
                    preparing_days.add(day)
                    break
            else:
                return -1

    return days


