def solution(stdin: str) -> str:
    n, k = map(int, stdin.split()[:2])
    s = stdin.split()[2]

    if k > n:
        return "-1"

    subseq_lengths = [1] * n
    for i in range(1, n):
        for j in range(i):
            if s[i] == s[j]:
                subseq_lengths[i] = max(subseq_lengths[i], subseq_lengths[j] + 1)

    sorted_subseq_lengths = sorted(subseq_lengths, reverse=True)
    total_cost = 0

    for length in sorted_subseq_lengths:
        if length > k:
            return str(total_cost)
        total_cost += n - length
        k -= length

    return "-1"


