        for i in range(len(s) - 1):
            if s[i] > s[i+1]:
                return False
        return True

    def swap_colors(colors: list, i: int, j: int):
        colors[i], colors[j] = colors[j], colors[i]

    for i in range(n):
        if colors[i] == 0 and s[i] not in colored_chars:
            colored_chars.add(s[i])
        if is_sorted(colors):
            continue
        for j in range(i+1, n):
            if colors[j] == colors[i]:
                continue
            if s[i] > s[j]:
                swap_colors(colors, i, j)
                if is_sorted(colors):
                    break
                else:
                    swap_colors(colors, i, j)
                    colors[j] = 1 - colors[j]
            else:
                swap_colors(colors, i, j)
                if is_sorted(colors):
                    break
                else:
                    swap_colors(colors, i, j)
                    colors[j] = 1 - colors[j]
        if is_sorted(colors):
            continue
        for j in range(i-1, -1, -1):
            if colors[j] == colors[i]:
                continue
            if s[i] < s[j]:
                swap_colors(colors, i, j)
                if is_sorted(colors):
                    break
                else:
                    swap_colors(colors, i, j)
                    colors[j] = 1 - colors[j]
            else:
                swap_colors(colors, i, j)
