def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    matrix = [int(x) for x in stdin.splitlines()[1].split()]
    numbers = set(matrix)
    min_num = min(numbers)
    max_num = max(numbers)
    if min_num < 1:
        return "NO"
    if max_num > 1000:
        return "NO"

    def is_palindromic(matrix: list, n: int) -> bool:
        for i in range(n):
            row = [matrix[i][j] for j in range(n)]
            if row != row[::-1]:
                return False
        for j in range(n):
            col = [matrix[i][j] for i in range(n)]
            if col != col[::-1]:
                return False
        return True

    for num in range(min_num, max_num + 1):
        if num not in numbers:
            continue
        numbers.remove(num)
        remaining_nums = sorted(numbers)
        remaining_nums = [x for x in remaining_nums if x != num]
        if not remaining_nums:
            return "NO"
        for i in range(1, n + 1):
            for j in range(1, n + 1):
                if matrix[i - 1][j - 1] == num:
                    matrix[i - 1][j - 1] = remaining_nums.pop(0)
                    if not is_palindromic(matrix, n):
                        matrix[i - 1][j - 1] = num
                        return "NO"
    return "YES"

