    def is_sorted(arr):
        for i in range(1, len(arr)):
            if arr[i - 1] > arr[i]:
                return False
        return True

    def invert_row(matrix, row_index):
        for i in range(len(matrix[row_index])):
            matrix[row_index][i] = 1 - matrix[row_index][i]

    def invert_col(matrix, col_index):
        for i in range(len(matrix)):
            matrix[i][col_index] = 1 - matrix[i][col_index]

    def perform_operations(matrix, row_inversions, col_inversions):
        for i in range(len(row_inversions)):
            if row_inversions[i]:
                invert_row(matrix, i)

        for i in range(len(col_inversions)):
            if col_inversions[i]:
                invert_col(matrix, i)

    def sort_matrix(matrix):
        arr = [matrix[i][j] for i in range(len(matrix)) for j in range(len(matrix[i]))]
        if is_sorted(arr):
            return False, [], []

        row_inversions = [False] * len(matrix)
        col_inversions = [False] * len(matrix[0])

