            for j in range(len(matrix[i])):
                if matrix[i][j] == 1:
                    continue

                if j != 0 and matrix[i][j - 1] == 1:
                    row_inversions[i] = True
                if i != 0 and matrix[i - 1][j] == 1:
                    col_inversions[j] = True

                matrix[i][j] = 1 - matrix[i][j]

            perform_operations(matrix, row_inversions, col_inversions)

        return True, row_inversions, col_inversions

