    def has_hamiltonian_cycle(node: int, visited: List[int]) -> bool:
        if visited[node] == -1:
            return False
        visited[node] = -1
        for neighbor in adj_list[node]:
            if not has_hamiltonian_cycle(neighbor, visited):
                return False
        return True

    # Check if the graph has a Hamiltonian cycle
    visited = [-1] * n
    if not has_hamiltonian_cycle(0, visited):
        return "NO"

    # Find a Hamiltonian cycle
    def find_hamiltonian_cycle(node: int, visited: List[int]) -> bool:
        visited[node] = 1
        for neighbor in adj_list[node]:
            if visited[neighbor] == -1 and find_hamiltonian_cycle(neighbor, visited):
                return True
        return False

    # Find a Hamiltonian cycle
    visited = [-1] * n
    if not find_hamiltonian_cycle(0, visited):
        return "NO"

    # Direct the edges in a way that the obtained directed graph does not contain paths of length at least two
    directed_edges = [0] * m
