    def dfs(node: int, comp: int, graph: list[list[int]], visited: set[int], comp_roads: list[int], company: int) -> tuple[int, int]:
        visited.add(node)
        comp_roads.append(node)
        for neighbor in graph[node]:
            if neighbor not in visited and neighbor != comp:
                new_comp, new_company = dfs(neighbor, comp, graph, visited, comp_roads, company)
                if new_comp == -1:
                    return -1, -1
                if new_company != company:
                    company = -1
        if comp_roads[0] == comp:
            return len(comp_roads), company
        else:
            return 0, company

    graph = [[] for _ in range(n)]
    for x, y in roads:
        graph[x-1].append(y-1)
        graph[y-1].append(x-1)

    max_comp = 1
    best_comp_roads = []
    best_company = 1
    for comp in range(1, n):
        for node in range(n):
            if node not in best_comp_roads:
                visited = set()
                comp_roads = []
                new_comp, new_company = dfs(node, comp, graph, visited, comp_roads, 1)
                if new_comp != -1:
                    if new_comp > max_comp:
                        max_comp = new_comp
                        best_comp_roads = comp_roads
                        best_company = new_company

    output = []
    for i in range(1, n):
        if i not in best_comp_roads:
            output.append(best_company)
        else:
            output.append(1)
    return f"{best_company}\n" + " ".join(map(str, output))

