    def min_cost(graph: List[List[int]], visited: List[bool], path: List[int], pos: int) -> int:
        if pos == len(path):
            return 0
        if visited[pos]:
            return float('inf')
        visited[pos] = True
        cost = float('inf')
        for i in range(len(graph[path[pos]])):
            if not visited[i]:
                cost = min(cost, graph[path[pos]][i] + min_cost(graph, visited, path, i))
        visited[pos] = False
        return cost

    n, m = read_ints()
    a = read_ints()
    graph = [[] for _ in range(n)]

    for _ in range(m):
        x, y, w = read_ints()
        graph[x - 1].append(w)
        graph[y - 1].append(w)

