    min_cost_of_connection = float('inf')

    for i in range(1, n):
        path = [0] + list(range(1, n))
        path[i], path[i + 1] = path[i + 1], path[i]
        visited = [False] * n
        min_cost_of_connection = min(min_cost_of_connection, total_cost - min_cost(graph, visited, path, i))

