        return sorted(segments, key=lambda x: (x[0], x[1]))

    def find_overlaps(segments: list) -> list:
        overlaps = []
        for i in range(len(segments)):
            for j in range(i + 1, len(segments)):
                if segments[i][0] <= segments[j][1] and segments[j][0] <= segments[i][1]:
                    overlaps.append((i, j))
        return overlaps

    def remove_overlaps(segments: list, overlaps: list) -> list:
        for i, j in overlaps:
            segments[i] = segments[i][0], min(segments[i][1], segments[j][1])
            segments[j] = max(segments[i][0], segments[j][0]), segments[j][1]
        return segments

    def find_bad_points(segments: list, k: int) -> set:
        bad_points = set()
        for i in range(len(segments)):
            for j in range(segments[i][0], segments[i][1] + 1):
                count = sum(1 for l, r in segments if l <= j <= r)
                if count > k:
                    bad_points.add(j)
        return bad_points

