    overlaps = find_overlaps(segments)
    segments = remove_overlaps(segments, overlaps)

    bad_points = find_bad_points(segments, k)
    m = len(bad_points)

    return f"{m}\n{' '.join(map(str, sorted(range(1, n + 1), key=lambda x: bad_points.issuperset(range(x)))))}"

