    def kruskal(n: int, edges: list, queries: list) -> str:
        edges.sort(key=lambda x: x[2])
        mst = []
        parent = list(range(n + 1))
        for edge in edges:
            u, v, w = edge
            pu = find_parent(parent, u)
            pv = find_parent(parent, v)
            if pu != pv:
                mst.append(edge)
                parent[pu] = pv
                if len(mst) == n - 1:
                    break

        result = []
        for q in queries:
            count = 0
            for u, v, w in mst:
                if w <= q:
                    count += (n * (n - 1) // 2) - ((n - 1) * (n - 2) // 2)
                else:
                    count += (n * (n - 1) // 2) - ((n - 1) * (n - 2) // 2)
                    break
            result.append(count)
        return result

