    def count_permutations(left, right):
        if left == right:
            return 1

        if left > right:
            return 0

        if left == 0:
            return count_permutations(left + 1, right)

        left_perms = count_permutations(left + 1, right)
        right_perms = count_permutations(left, right - 1)
        return (left_perms + right_perms) * (right - left + 1) % MOD

    total_perms = count_permutations(0, N - 1)
    if total_perms == 0:
        return "0"

    possible_orders = 0
    for a in A:
        left_orders = count_permutations(0, a - 1)
        right_orders = count_permutations(a + 1, N - 1)
        possible_orders = (possible_orders * left_orders * right_orders) % MOD

    return str(possible_orders)

