    roads = [tuple(map(int, stdin.split()[3:6])) for _ in range(m)]
    routes = [tuple(map(int, stdin.split()[6+i:6+i+2])) for i in range(k)]

    graph = {i: set() for i in range(1, n+1)}
    for x, y, w in roads:
        graph[x].add((y, w))
        graph[y].add((x, w))

    def dijkstra(graph, start, end, seen, distances):
        if start not in graph:
            return float('inf')
        seen.add(start)
        for neighbor, weight in graph[start]:
            if neighbor not in seen:
                new_distance = distances[start] + weight
                if new_distance < distances[neighbor]:
                    distances[neighbor] = new_distance
                    if neighbor == end:
                        return distances[end]
                seen.add(neighbor)
        return min(dijkstra(graph, neighbor, end, seen, distances) for neighbor in graph[start])

    total_cost = sum(dijkstra(graph, a, b, set(), {i: float('inf') for i in range(1, n+1)}) for a, b in routes)

    return str(total_cost)

