        elif real == 0:
            return f"0.00+{imag:.2f}i"
        else:
            sign = "-" if imag < 0 else "+"
            return f"{real:.2f}{sign}{abs(imag):.2f}i"

    def complex_operation(op: str, x: complex, y: complex) -> complex:
        return {
            "+": x + y,
            "-": x - y,
            "*": x * y,
            "/": x / y,
        }[op]

