    def play_game(decks: list, current_player: str) -> str:
        while decks[0] and decks[1] and decks[2]:
            top_cards = [deck[-1] for deck in decks]
            next_players = [next_player(current_player, top_card) for top_card in top_cards]
            decks = [deck[:-1] for deck in decks]
            current_player = next_players[0]

        if not decks[0]:
