    move = int(input_lines[4])

    def merge_tiles(board):
        for i in range(4):
            for j in range(3):
                if board[i][j] == 0 and board[i][j+1] != 0:
                    board[i][j], board[i][j+1] = board[i][j+1], board[i][j]
                    board[i][j+1] = 0
                elif board[i][j] != 0 and board[i][j+1] == board[i][j]:
                    board[i][j] *= 2
                    board[i][j+1] = 0
                elif board[i][j] != 0 and board[i][j+1] == 0:
                    for k in range(j+1, 3):
                        if board[i][k] != 0:
                            board[i][k-1] = board[i][k]
                            board[i][k] = 0
                            break
        return board

    def move_tiles(board, direction):
        if direction == 0:
            board = [[board[i][j] for j in range(4)] for i in range(4)]
        elif direction == 1:
            board = [[board[j][i] for j in range(4)] for i in range(4)]
        elif direction == 2:
            board = [[board[i][3-j] for j in range(4)] for i in range(4)]
        else:
            board = [[board[3-i][j] for j in range(4)] for i in range(4)]
        return board

    def add_new_tile(board):
        empty_cells = [(i, j) for i in range(4) for j in range(4) if board[i][j] == 0]
        if empty_cells:
            i, j = random.choice(empty_cells)
            board[i][j] = 2 if random.random() < 0.9 else 4
        return board

    for _ in range(move):
        board = move_tiles(board, move)
        board = merge_tiles(board)
        board = add_new_tile(board)

    output = '\n'.join([' '.join(map(str, row)) for row in board])
    return output

