    # Create a dictionary to store the number of socks of each type
    sock_types = {}
    for sock in socks:
        if sock in sock_types:
            sock_types[sock] += 1
        else:
            sock_types[sock] = 1

    # Initialize the auxiliary pile
    aux = []

    # Calculate the number of moves needed to pair the socks
    moves = 0
    for sock_type, count in sock_types.items():
        if count % 2 == 1:
            moves += 1
            aux.append(sock_type)

    # If there are more than one odd sock types, it is impossible to pair them
    if len(aux) > 1:
        return "impossible"

    # Move the odd socks to the auxiliary pile
    while aux:
        moves += 1
        socks.append(aux.pop())

    # Pair the socks
    for i in range(len(socks) - 1, -1, -2):
        if socks[i] == socks[i - 1]:
            moves += 1
            socks.pop(i - 1)
            socks.pop(i - 1)

    return moves

