def solution(stdin: str) -> str:
    t, N, M, grid = 0, 0, 0, []
    for i, line in enumerate(stdin.split("\n")):
        if i == 0:
            t, N, M = map(int, line.split())
        else:
            grid.append(list(line))

    directions = {"U": (-1, 0), "D": (1, 0), "L": (0, -1), "R": (0, 1)}

    def is_burning(x, y):
        return grid[y][x] == "1"

    def is_safe(x, y):
        return not is_burning(x, y) and grid[y][x] != "S"

    def is_valid(x, y):
        return 0 <= x < N and 0 <= y < M

    def is_possible_to_escape(x, y):
        if not is_safe(x, y) or not is_valid(x, y):
            return False
        for d in directions.values():
            dx, dy = d
            if is_safe(x + dx, y + dy):
                return True
        return False

    def is_safe_to_enter(x, y, direction):
        if not is_valid(x, y) or not is_safe(x, y):
            return False
        dx, dy = directions[direction]
        if is_safe(x + dx, y + dy):
            return True
        return False

    def escape_time(x, y):
        if is_burning(x, y):
            return 1000000000
        if grid[y][x] == "S":
            return 0
        return 1

    def escape(x, y, visited):
        if (x, y) in visited:
            return visited[(x, y)]

        visited[(x, y)] = float("inf")
        for d in directions.keys():
            if is_safe_to_enter(x, y, d):
                dx, dy = directions[d]
                if is_burning(x + dx, y + dy):
                    visited[(x, y)] = min(visited[(x, y)], escape(x + dx, y + dy, visited))
                else:
                    visited[(x, y)] = min(visited[(x, y)], escape(x + dx, y + dy, visited) + 1)

        return visited[(x, y)]

    result = escape(N - 1, M - 1, {})
    if result >= t:
        return "NOT POSSIBLE"
    else:
        return result

