    player_position = None
    gold_count = 0
    safe_squares = set()
    unsafe_squares = set()

    for i in range(H):
        for j in range(W):
            if map_grid[i][j] == 'P':
                player_position = (i, j)
            elif map_grid[i][j] == 'G':
                gold_count += 1
                safe_squares.add((i, j))
            elif map_grid[i][j] == 'T':
                unsafe_squares.add((i, j))
            elif map_grid[i][j] == '#':
                continue
            else:
                safe_squares.add((i, j))

    if not player_position:
        raise ValueError("Player position not found in map.")

    def is_safe(x, y):
        return (x, y) in safe_squares

    def is_unsafe(x, y):
        return (x, y) in unsafe_squares

    def bfs(x, y):
        queue = [(x, y)]
        visited = set()

        while queue:
            x, y = queue.pop(0)
            visited.add((x, y))

            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < H and 0 <= ny < W and map_grid[nx][ny] != '#' and (nx, ny) not in visited and is_safe(nx, ny):
                    queue.append((nx, ny))

        return visited

    def get_gold_count(x, y):
        gold_count = 0
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < H and 0 <= ny < W and map_grid[nx][ny] == 'G':
                gold_count += 1
        return gold_count

    def get_safest_position(x, y):
        visited = bfs(x, y)
        safest_position = None
        max_gold_count = 0

        for i, j in visited:
            gold_count_at_position = get_gold_count(i, j)
            if gold_count_at_position > max_gold_count:
                max_gold_count = gold_count_at_position
                safest_position = (i, j)

        return safest_position

    def count_safest_gold(x, y):
        safest_position = get_safest_position(x, y)
        return get_gold_count(safest_position[0], safest_position[1]) if safest_position else 0

    # Starting from player position, count gold safely
    gold_count = count_safest_gold(*player_position)

    return str(gold_count)

