        total_probability = 0.0
        for password in passwords:
            total_probability += password[1]
        probabilities = []
        current_probability = 0.0
        for password in passwords:
            current_probability += password[1]
            probabilities.append((password[0], current_probability / total_probability))
        return probabilities

    def expected_attempts(passwords: list) -> float:
        probabilities = compute_probabilities(passwords)
        expected_attempts = 0.0
        for i in range(len(probabilities)):
            expected_attempts += (i + 1) * probabilities[i][1]
        return expected_attempts

