    def divide(message: str) -> tuple[str, str]:
        return message[:len(message) // 2], message[len(message) // 2:]

    def rotate(message: str) -> str:
        rotation_values = [ord(c) - ord('A') for c in message]
        total_rotation = sum(rotation_values)
        rotated = [chr(((ord(c) - ord('A') + total_rotation) % 26) + ord('A')) for c in message]
        return ''.join(rotated)

    def merge(left: str, right: str) -> str:
        merged = [chr(((ord(left[i]) - ord('A') + ord(right[i]) - ord('A')) % 26) + ord('A')) for i in range(len(left))]
        return ''.join(merged)

    def decrypt(message: str) -> str:
        left, right = divide(message)
        left_rotated = rotate(left)
        right_rotated = rotate(right)
        merged = merge(left_rotated, right_rotated)
        return merged

