def solution(stdin: str) -> str:
    input_data = stdin.splitlines()
    n, s = map(int, input_data.pop(0).split())
    holes = [tuple(map(int, line.split())) for line in input_data]

    def is_inside_hole(x, y, z):
        for hole in holes:
            r, hx, hy, hz = hole
            dx, dy, dz = x - hx, y - hy, z - hz
            if dx**2 + dy**2 + dz**2 <= r**2:
                return True
        return False

    def is_inside_block(x, y, z):
        return 0 <= x <= 100000 and 0 <= y <= 100000 and 0 <= z <= 100000 and not is_inside_hole(x, y, z)

    def calculate_slice_thickness():
        for i in range(s):
            z = 100 * i
            block_below_slice = sum(is_inside_block(x, y, z - 1) for x in range(100) for y in range(100))
            slice_thickness = block_below_slice / 100
            yield slice_thickness

    return "\n".join(f"{round(thickness, 6):.6f}" for thickness in calculate_slice_thickness())

