def solution(stdin: str) -> str:
    n = int(stdin)
    characters = []

    for _ in range(n):
        line = input().split()
        name = line[0]
        language = line[1]
        languages = line[2:]
        characters.append({"name": name, "language": language, "languages": languages})

    seen = set()
    visited = set()
    min_leave = float('inf')

    def dfs(current, target):
        if current in visited:
            return
        visited.add(current)

        nonlocal min_leave
        if target in seen:
            min_leave = min(min_leave, len(seen))

        for character in characters:
            if character["name"] != current and character["language"] in character["languages"]:
                dfs(character["name"], target)

        seen.add(current)

    for character in characters:
        dfs(character["name"], character["language"])

    return min_leave

