            return 0
        min_cost = float('inf')
        for i, hotel in enumerate(hotels):
            if hotel['price'] > budget:
                continue
            if hotel['beds'] < n:
                continue
            min_cost = min(min_cost, hotel['price'] + min_cost(n-1, budget - hotel['price'], hotels[:i] + hotels[i+1:]))
