    input_lines = stdin.split('\n')
    n, m = map(int, input_lines[0].split())
    map_data = [list(line) for line in input_lines[1:]]
    if sum(map_data[0].count('.') for row in map_data) == 0:
        raise ValueError("No land cells found in the map")

    def find_neighbors(row: int, col: int) -> list:
        neighbors = []
        if row > 0:
            neighbors.append((row-1, col))
        if row < n-1:
            neighbors.append((row+1, col))
        if col > 0:
            neighbors.append((row, col-1))
        if col < m-1:
            neighbors.append((row, col+1))
        return neighbors

    def check_adjacent(cells: set, row: int, col: int) -> bool:
        for neighbor_row, neighbor_col in find_neighbors(row, col):
            if (neighbor_row, neighbor_col) in cells:
                return True
        return False

    def find_placement() -> list:
        placed_cells = set()
        for row in range(n):
            for col in range(m):
                if map_data[row][col] == '.':
                    if not check_adjacent(placed_cells, row, col):
                        placed_cells.add((row, col))
                        map_data[row][col] = 'E'
                    else:
                        raise ValueError("No valid placement found")
        return map_data

    try:
        placement = find_placement()
    except ValueError as e:
        raise ValueError("No valid placement found") from e

    return '\n'.join(''.join(row) for row in placement)

