def solution(stdin: str) -> str:
    n = int(stdin.split('\n')[0])
    temperatures = list(map(int, stdin.split('\n')[1].split()))

    # If the first and last pool temperatures are the same, there is no solution
    if temperatures[0] == temperatures[-1]:
        return "impossible"

    # Find the index of the first pool with a lower temperature than the previous one
    first_index = 0
    for i in range(1, n):
        if temperatures[i] < temperatures[i-1]:
            first_index = i
            break

    # If the first pool has the lowest temperature, reverse the sequence
    if first_index == 0:
        return ' '.join(str(t) for t in temperatures[::-1])

    # If the last pool has the lowest temperature, reverse the sequence and swap the first and last pools
    if first_index == n-1:
        return f"{temperatures[-1]} {' '.join(str(t) for t in temperatures[1:-1][::-1])} {temperatures[0]}"

    # If the first pool has a higher temperature than the previous one, reverse the sequence and swap the first and last pools
    if temperatures[first_index] > temperatures[first_index-1]:
        return f"{temperatures[-1]} {' '.join(str(t) for t in temperatures[1:-1][::-1])} {temperatures[0]}"

    # Find the index of the last pool with a higher temperature than the previous one
    last_index = n-1
    for i in range(n-2, -1, -1):
        if temperatures[i] > temperatures[i+1]:
            last_index = i
            break

    # If the last pool has the highest temperature, reverse the sequence
    if last_index == n-1:
        return ' '.join(str(t) for t in temperatures[::-1])

    # If the first pool has the highest temperature, reverse the sequence and swap the first and last pools
    if last_index == 0:
        return f"{temperatures[-1]} {' '.join(str(t) for t in temperatures[1:][::-1])} {temperatures[0]}"

    # If the first and last pools have the same temperature, there is no solution
    if temperatures[first_index] == temperatures[last_index]:
        return "impossible"

    # Swap the first and last pools if necessary
    if temperatures[0] < temperatures[-1]:
        temperatures[0], temperatures[-1] = temperatures[-1], temperatures[0]

    # Find the indices of the pools to swap
    swap_index = n-1
    for i in range(last_index, first_index+1):
        if temperatures[i] > temperatures[swap_index]:
            swap_index = i

    # Swap the pools
    temperatures[first_index], temperatures[swap_index] = temperatures[swap_index], temperatures[first_index]

    # Check if the result satisfies the requirement
    for i in range(1, n-1):
        if temperatures[i] - temperatures[i-1] > temperatures[i+1] - temperatures[i]:
            return "impossible"

    return ' '.join(str(t) for t in temperatures)

