    N = int(N)
    board = [list(row) for row in board]

    def is_valid(r, c):
        return 1 <= r <= N and 1 <= c <= N and board[r-1][c-1] != '#'

    def bfs(r, c, target_r, target_c):
        visited = set()
        queue = [(r, c, 0)]

        while queue:
            r, c, steps = queue.pop(0)
            if (r, c) == (target_r, target_c):
                return steps

            for dr, dc in [(2, 1), (2, -1), (1, 2), (1, -2), (-2, 1), (-2, -1), (-1, 2), (-1, -2)]:
                rr, cc = r + dr, c + dc
                if is_valid(rr, cc):
                    queue.append((rr, cc, steps + 1))
                    visited.add((rr, cc))

        return -1

    for r in range(1, N+1):
        for c in range(1, N+1):
            if board[r-1][c-1] == 'K':
                return bfs(r, c, 1, 1)

