def solution(stdin: str) -> str:
    # Read input
    key_phrase, plaintext = stdin.split('\n')

    # Remove spaces and duplicate letters from key phrase
    key_phrase = ''.join(sorted(set(key_phrase.lower())))

    # Fill remaining spaces with the rest of the letters of the alphabet
    key_phrase += ''.join(sorted(set('abcdefghijklmnopqrstuvwxyz') - set(key_phrase)))

    # Create encryption table
    table = [[0] * 5 for _ in range(5)]
    row, col = 0, 0
    for letter in key_phrase:
        if letter == 'j':
            table[row][col] = 'i'
            col += 1
        elif letter == 'x':
            table[row][col] = 'z'
            col += 1
        else:
            table[row][col] = letter
            col += 1
        if col == 5:
            col = 0
            row += 1

    # Encrypt plaintext
    encrypted = ''
    for i in range(0, len(plaintext), 2):
        if plaintext[i] == 'x':
            encrypted += plaintext[i]
        else:
            letter1 = plaintext[i]
            letter2 = plaintext[i + 1] if i < len(plaintext) - 1 else 'x'

            if letter1 == letter2:
                encrypted += letter1 + 'x'
            elif (row1, col1) == (row2, col2) or (row1 == row2 and col1 == col2):
                encrypted += table[row1][col1 + 1] + table[row1 + 1][col1]
            else:
                encrypted += table[row1][col2] + table[row2][col1]

    # Return encrypted text
    return encrypted.upper()

