    adj_list = [[] for _ in range(n)]
    for i in range(n):
        if f[i] != 0:
            adj_list[f[i] - 1].append(i)

    # Find the number of gifts each friend should give
    gifts_to_give = [-1] * n
    for i in range(n):
        if f[i] == 0:
            if len(adj_list[i]) == 1:
                # If the friend has exactly one friend who wants to give the gift to, give a gift to that friend
                gifts_to_give[i] = f[adj_list[i][0]]
            else:
                # If the friend has more than one friend who wants to give the gift to, give a gift to all of them
                for friend in adj_list[i]:
                    gifts_to_give[friend] = n

    # Find the number of gifts each friend should receive
    gifts_to_receive = [0] * n
    for i in range(n):
        if gifts_to_give[i] != -1:
            # If the friend has already given a gift, remove it from the total number of gifts
            for friend in adj_list[i]:
                gifts_to_receive[friend] += gifts_to_give[i] - 1
            # Add the number of gifts the friend should give to the total number of gifts
            gifts_to_receive[i] += gifts_to_give[i]

    # Print the final result
    result = [str(n * gifts_to_receive[i]) for i in range(n)]
