    input_str = stdin.split('\n')
    n, m = map(int, input_str[0].split())
    exams = []

    for line in input_str[1:]:
        s_i, d_i, c_i = map(int, line.split())
        exams.append((s_i, d_i, c_i))

    def is_possible(exams, day):
        for exam in exams:
            if exam[0] <= day < exam[1]:
                return False
        return True

    def count_days(exams):
        days = 0
        for exam in exams:
            days += exam[2]
        return days

    def is_valid(exams, days):
        if count_days(exams) != days:
            return False
        for i in range(len(exams)):
            if exams[i][0] > exams[i][1]:
                return False
        return True

    def prepare_exams(exams, days):
        schedule = [0] * days
        for exam in exams:
            s_i, d_i, c_i = exam
            for day in range(s_i - 1, d_i - c_i - 1):
                if is_possible(exams, day):
                    schedule[day] = len(exams)
                    break
        return schedule

    if not is_valid(exams, n):
        return -1

    days = count_days(exams)
    schedule = prepare_exams(exams, days)
    for day in range(days):
        if schedule[day] == 0:
            schedule[day] = m + 1
        elif schedule[day] == len(exams):
            schedule[day] = 0

    return ' '.join(map(str, schedule))

